import VueRouter from 'vue-router'

const routes=[
    {
        path:'/',
        name:'login',
        component:()=>import('../components/Login')
    },
    {
        path: '/Index',
        name:'index',
        component:()=>import('../components/Index'),
        children:[
            {
                path:'/Home',
                name:'home',
                meta:{
                  title:'个人中心'
                },
                component:()=>import('../components/Home')
            },
            {
                path: '/Main',
                name:'main',
                meta:{
                    title: '首页'
                },
                component:()=>import('../components/Main')
            },
            // {
