import "core-js/modules/es.array.push.js";
// import axios from "axios";axios

import { handleExportNum } from "@/options/ExportExcel";
export default {
  // eslint-disable-next-line vue/multi-word-component-names
  name: 'Goods',
  data() {
    //校验物品类型是否已存在
    let checkDuplicate = (rule, value, callback) => {
      if (this.form.id) {
        return callback();
      }
      this.$axios.get(this.$httpUrl + "/goods/findByName?name=" + this.form.name).then(res => res.data).then(res => {
        if (res.code !== 200) {
          callback();
        } else {
          callback(new Error('类型已经存在'));
        }
      });
    };
    return {
      is_add: true,
      title_name: '',
      list_loading: false,
      tableData: [],
      pageNum: 1,
      pageSize: 10,
      total: 0,
      name: '',
      dialogVisible: false,
      //导出选择框
      value: 0,
      //滑条数值
      checked: false,
      //是否选择全部数据导出
      exportStep: 0,
      //滑条步长

      centerDialogVisible: false,
      form: {
        id: '',
        name: '',
        remark: ''
      },
      //表单输入规则
      rules: {
        name: [{
          required: true,
          message: '请输入物品类型',
          trigger: 'blur'
        }, {
          validator: checkDuplicate,
          trigger: "blur"
        }]
      }
    };
  },
  beforeMount() {
    this.loadPost();
  },
  methods: {
    //导出
    handle() {
      //处理步长为十等分
      this.dialogVisible = true;
      this.exportStep = handleExportNum(this.total, this.exportStep);
    },
    handleClose() {
      //关闭窗口后清除value
      this.value = 0;
      this.dialogVisible = false;
    },
    exportData() {
      let exportNum = 0;
      if (this.checked) {
        exportNum = this.total;
      } else {
        exportNum = this.value;
      }
      this.dialogVisible = false;
      this.$message.warning('正在导出数据,请稍等~');
      this.$axios.post(this.$httpUrl + '/goods/listPage1', {
        param: {
          name: this.name
        },
        pageNum: this.pageNum,
        pageSize: exportNum
      }).then(res => {
        // console.log(res, '====')
        if (res.data.code === 200) {
          const columns = res.data.data;
          const columnsData = [];
          columns.forEach(row => {
            const sums = {
              id: row.id,
              name: row.name,
              remark: row.remark
            };
            columnsData.push(sums);
          });
          this.handleDownload(columnsData);
        } else {
          this.$message.error(res.msgs);
        }
      });
    },
    handleDownload(tableData) {
      import('@/options/ExportExcel').then(excel => {
        const tHeader = ['id', '物品类型', '备注'];
        const filterVal = ['id', 'name', 'remark'];
        const data = this.formatJson(filterVal, tableData);
        const date = new Date();
        const y = date.getFullYear();
        let m = date.getMonth() + 1;
        let d = date.getDate();
        let h = date.getHours();
        let mm = date.getMinutes();
        let s = date.getSeconds();
        const time = y + '-' + m + '-' + d + ' ' + h + ':' + mm + ':' + s;
        excel.export_json_to_excel({
          header: tHeader,
          data,
          filename: `物品类型列表(${time})`
        });
        this.$message.success('导出成功');
      });
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => {
        const temp_keys = j.split('.');
        if (temp_keys.length > 1) {
          let temp_result = v;
          for (const m of temp_keys) {
            temp_result = temp_result[m] ? temp_result[m] : '';
            if (temp_result === '') break;
          }
          return temp_result;
        } else {
          return v[j];
        }
      }));
    },
    //点击表单的取消按钮，清除验证结果

    loadPost() {
      this.list_loading = true;
      this.$axios.post(this.$httpUrl + '/goods/listPage1', {
        param: {
          name: this.name
        },
        pageNum: this.pageNum,
        pageSize: this.pageSize
      }).then(res => res.data).then(res => {
        console.log(res.data);
        if (res.code === 200) {
          //判断状态码是否200
          //结果集的数据传入tableData
          this.tableData = res.data;
          this.total = res.total;
        } else {
          alert("查询失败" + res.code);
        }
        this.list_loading = false;
        // this.tableData=res.data
        // console.log(res)
      }).catch(e => {
        console.log(e);
        this.list_loading = false;
      });
    },
    //查询
    search() {
      //每次查询后显示第一页
      this.pageNum = 1;
      this.loadPost();
    },
    //重置参数
    reSet() {
      this.name = '';
      this.loadPost();
    },
    //新增
    add() {
      this.title_name = '新增';
      this.centerDialogVisible = true;
      this.is_add = true;
      this.$nextTick(() => {
        this.resetForm();
      });
    },
    //点击编辑按钮，从表格拿到数据传给form
    mod(row) {
      this.title_name = '编辑';
      this.is_add = false;
      this.form.id = row.id;
      this.form.name = row.name;
      this.form.remark = row.remark;
      // this.rules.password[0].required=false;
      // this.rules.checkPass.required=false;
      this.centerDialogVisible = true;
    },
    //删除
    del(id, name) {
      console.log(id);
      this.$confirm('是否删除当前用户信息？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
        closeOnClickModal: false
      }).then(() => {
        //  接口 点击确定就会走then
        this.$axios.get(this.$httpUrl + '/goods/delete?id=' + id);
        this.$message({
          message: '删除物品类型[' + name + ']成功~~~~~~~~~~~~~~~~~',
          type: 'success'
        });
        console.log('dedede', name);
        this.loadPost();
      }).catch(e => {
        // 取消就会走catch
        console.log(e);
      });
    },
    save() {
      //输入格式正确则保存
      this.$refs.form.validate(valid => {
        if (valid) {
          //开始保存
          this.$axios.post(this.$httpUrl + '/goods/save', this.form).then(res => res.data).then(res => {
            console.log(res.data);
            if (res.code === 200) {
              //判断状态码是否200
              if (this.is_add) {
                this.$message({
                  message: '新增物品类型成功~~~',
                  type: 'success'
                });
              } else {
                this.$message({
                  message: '修改物品类型成功~~~',
                  type: 'success'
                });
              }
              this.loadPost();
              this.centerDialogVisible = false;
            } else {
              //状态码不为200，保存失败
              this.$message({
                message: '新增物品类型失败！！！',
                type: 'error'
              });
            }
          });
        } else {
          this.$message({
            message: "请规范填写信息",
            type: 'error'
          });
          return false;
        }
      });
    },
    closeDialog() {
      this.centerDialogVisible = false;
      this.$refs.form.clearValidate();
    },
    resetForm() {
      this.form.id = ''; //清空id
      this.form.name = ''; //清空name
      this.form.remark = ''; //清空remark
      console.log('id', this.form.id);
    },
    handleSizeChange(val) {
      console.log(`每页 ${val} 条`);
      this.pageSize = val;
      this.pageNum = 1;
      this.loadPost();
    },
    handleCurrentChange(val) {
      console.log(`当前页: ${val}`);
      this.pageNum = val;
      this.loadPost();
    }
  }
};
