import vue from 'vue'
import Vuex from 'vuex'
import router from "../router"

vue.use(Vuex)

function addNewRoute(menuList) {
    // console.log(menuList, '111')
    let routes = router.options.routes
    // console.log(routes, '2222')
    routes.forEach(routeItem => {
        if (routeItem.path === "/Index") {
            routeItem.children.forEach(chil => {
                if (chil.name !== 'home' && chil.name !== 'main') {
                    menuList.forEach(menu => {
                        let childRoute = {
                            path: '/' + menu.menuclick,
                            name: menu.menuclick,
                            meta: {
                                title: menu.menuname
                            },
                            component: () => import('../components/' + menu.menucomponent)
                        }
                        router.addRoute(childRoute)
                    })
                }
            })


        }
    })
    // resetRouter()
    // for (let x of routes) {
    //     if (x.name !== 'login') {
    //         x.children.forEach(chil => {
    //             console.log(chil, 'cccccccc')
    //             if (chil.name !== 'home' && chil.name !== 'main') {
    //                 router.addRoute(chil)
    //             }
    //         })
    //     }
    // }
    console.log(routes, '888888888888')
}

export default new Vuex.Store({
    state: {
        menu: []
    },
    mutations: {
        setMenu(state, menuList) {
            state.menu = menuList
            addNewRoute(menuList)
        }
    },
    getters: {
        getMenu(state) {
            return state.menu
        }
    }
})
